mapper.select:
select * from ( select rownum rn, b.*, to_char(bi_price, '999,999') as bi_cprice, (select count(*) from bsreply r where r.bi_num=b.bi_num and r.lvl=0 ) as rcnt, ROUND(NVL((select sum(r_stars)/count(*) from bsreply r where r.bi_num=b.bi_num and r.lvl=0),0),1) as avpnt, (select NVL(sum(p_amount),0) from purchasedetail p where p.p_bnum = b.bi_num) as sales from (select * from bookinfo where regexp_like(bi_title||bi_content, '아두이노|안드로이드|모바일|임베디드')) b order by
bi_pdate desc bi_price asc rcnt desc avpnt desc sales desc bi_num desc
) where rn between #{liststart} and #{listend}

select * from ( select rownum rn, b.*, to_char(bi_price, '999,999') as bi_cprice, (select count(*) from bsreply r where r.bi_num=b.bi_num and r.lvl=0 ) as rcnt, ROUND(NVL((select sum(r_stars)/count(*) from bsreply r where r.bi_num=b.bi_num and r.lvl=0),0),1) as avpnt, (select NVL(sum(p_amount),0) from purchasedetail p where p.p_bnum = b.bi_num) as sales from (select * from bookinfo where regexp_like(bi_title||bi_content,
'아두이노|안드로이드|모바일|임베디드' '웹디자인|html|css|javascript|jQuery|bootstrap|자바스크립트|제이쿼리|부트스트랩', 'i' '프로그래밍|C언어|java|자바|JSP|Ajax|xml' , 'i') and not regexp_like(bi_title, '자바스크립트|javascript|웹', 'i' '오라클|데이터베이스|db|mysql|oracle' , 'i' '웹 서비스|스프링|프레임워크' , 'i' '나중에 #{query}로 수정'
)) b order by
bi_pdate desc bi_price asc rcnt desc avpnt desc sales desc bi_num desc
) where rn between #{liststart} and #{listend}
